  showList(cardName: string, index: number) {
    const nameOfList = cardName;
    this.listName.set(nameOfList.valueOf());
    this.whatList(index);

    let key = '';
    switch (index) {
        case 0:
            key = 'votantesPrimeraLista';
            break;
        case 1:
            key = 'votantesSegundaLista';
            break;
        case 2:
            key = 'votantesTerceraLista';
            break;
        case 3:
            key = 'votantesCuartaLista';
            break;
        case 4:
            key = 'votantesQuintaLista';
            break;
    }

    const item = localStorage.getItem(key);
    let currentData = item ? JSON.parse(item) : [];
    if (!Array.isArray(currentData)) { 
        currentData = [currentData]; 
    }
    this.candidates.update(() => currentData);
  }

